import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Avatar,
  Chip,
  IconButton,
} from '@mui/material';
import {
  MicRounded,
  MusicNoteRounded,
  PersonRounded,
} from '@mui/icons-material';
import { apiService, Song } from '../services/api';
import GuitarLoader from './GuitarLoader';

const MainWindow: React.FC = () => {
  const navigate = useNavigate();
  const [recentSongs, setRecentSongs] = useState<Song[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadRecentSongs = async () => {
      try {
        setLoading(true);
        setError(null);
        const songs = await apiService.getRecentSongs(3);
        setRecentSongs(songs || []);
      } catch (err) {
        console.error('Failed to load recent songs:', err);
        setError('Kh√¥ng th·ªÉ t·∫£i danh s√°ch b√†i h√°t g·∫ßn ƒë√¢y');
        // Fallback to mock data
        setRecentSongs([
          { 
            id: 1, 
            title: 'M∆∞a Phi Tr∆∞·ªùng', 
            artist: { id: 1, name: 'Ho√†ng D≈©ng' }, 
            lyrics: '',
            tempo: 80,
            difficulty: 'BEGINNER',
            playCount: 0,
            chords: []
          }
        ]);
      } finally {
        setLoading(false);
      }
    };

    loadRecentSongs();
  }, []);

  const handleFamiliarSongs = () => {
    if (recentSongs && recentSongs.length > 0) {
      const randomSong = recentSongs[Math.floor(Math.random() * recentSongs.length)];
      if (randomSong?.id) {
        navigate(`/player/${randomSong.id}`);
      }
    } else {
      navigate('/search');
    }
  };

  const handleVoiceSearch = () => {
    navigate('/search');
  };

  const handleSongSelect = (songId: number) => {
    if (songId) {
      navigate(`/player/${songId}`);
    }
  };

  const chordProgressions = [
    { name: 'Canon', chords: 'C G Am Em F C F G', icon: MusicNoteRounded },
    { name: 'Pop Ballad', chords: 'G Em C D', icon: MusicNoteRounded },
    { name: 'Blues', chords: 'A D E A', icon: MusicNoteRounded },
    { name: 'Bolero', chords: 'Am Dm', icon: MusicNoteRounded },
  ];

  return (
    <Box sx={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      p: 0
    }}>
      {/* Hero Section */}
      <Box sx={{ 
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        color: 'white',
        p: 4,
        pb: 6
      }}>
        <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
          <Box>
            <Typography variant="h3" sx={{ mb: 1 }}>
              üé∏ Guitar Chords
            </Typography>
            <Typography variant="body1" sx={{ opacity: 0.9 }}>
              Master Vietnamese guitar songs with ease
            </Typography>
          </Box>
          <Avatar sx={{ 
            width: 48, 
            height: 48, 
            background: 'rgba(255,255,255,0.2)',
            backdropFilter: 'blur(10px)'
          }}>
            <PersonRounded />
          </Avatar>
        </Box>

        {/* Search Bar */}
        <Card sx={{ 
          background: 'rgba(255,255,255,0.95)', 
          backdropFilter: 'blur(10px)',
          border: 'none',
          boxShadow: '0 8px 32px rgba(0,0,0,0.1)'
        }}>
          <Box 
            onClick={handleVoiceSearch}
            sx={{
              p: 3,
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              gap: 2,
              '&:hover': {
                background: 'rgba(102, 126, 234, 0.05)'
              }
            }}
          >
            <Box sx={{
              width: 48,
              height: 48,
              borderRadius: '50%',
              background: 'linear-gradient(135deg, #667eea, #764ba2)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white'
            }}>
              <MicRounded />
            </Box>
            <Box flex={1}>
              <Typography variant="h6" color="primary.main">
                T√¨m ki·∫øm b√†i h√°t ho·∫∑c h·ª£p √¢m
              </Typography>
              <Typography variant="body2" color="text.secondary">
                S·ª≠ d·ª•ng gi·ªçng n√≥i ho·∫∑c g√µ t√™n b√†i h√°t
              </Typography>
            </Box>
          </Box>
        </Card>
      </Box>

      {/* Content Section */}
      <Box sx={{ 
        backgroundColor: 'background.default',
        borderTopLeftRadius: 24,
        borderTopRightRadius: 24,
        mt: -3,
        position: 'relative',
        zIndex: 1,
        p: 3,
        minHeight: '60vh'
      }}>

        {/* Quick Actions */}
        <Box mb={4}>
          <Typography variant="h6" sx={{ mb: 2, color: 'text.primary' }}>
            B·∫Øt ƒë·∫ßu nhanh
          </Typography>

          <Card 
            onClick={handleFamiliarSongs}
            sx={{
              cursor: 'pointer',
              transition: 'all 0.2s ease',
              '&:hover': {
