import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Avatar,
  Chip,
  IconButton,
  Button,
  Fab,
} from '@mui/material';
import { apiService, Song } from '../services/api';
import GuitarLoader from './GuitarLoader';

const MainWindow: React.FC = () => {
  const navigate = useNavigate();
  const [recentSongs, setRecentSongs] = useState<Song[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadRecentSongs = async () => {
      try {
        setLoading(true);
        setError(null);
        const songs = await apiService.getRecentSongs(3);
        setRecentSongs(songs || []);
      } catch (err) {
        console.error('Failed to load recent songs:', err);
        setError('Không thể tải danh sách bài hát gần đây');
        // Fallback to mock data
        setRecentSongs([
          { 
            id: 1, 
            title: 'Nơi Này Có Anh', 
            artist: { id: 1, name: 'Sơn Tùng M-TP' }, 
            lyrics: '',
            tempo: 80,
            difficulty: 'BEGINNER',
            playCount: 0,
            chords: []
          },
          { 
            id: 2, 
            title: 'Chạy Ngay Đi', 
            artist: { id: 1, name: 'Sơn Tùng M-TP' }, 
            lyrics: '',
            tempo: 90,
            difficulty: 'INTERMEDIATE',
            playCount: 0,
            chords: []
          },
          { 
            id: 3, 
            title: 'Có Chắc Yêu Là Đây', 
            artist: { id: 1, name: 'Sơn Tùng M-TP' }, 
            lyrics: '',
            tempo: 85,
            difficulty: 'BEGINNER',
            playCount: 0,
            chords: []
          },
          { 
            id: 4, 
            title: 'Hãy Trao Cho Anh', 
            artist: { id: 1, name: 'Sơn Tùng M-TP' }, 
            lyrics: '',
            tempo: 95,
            difficulty: 'INTERMEDIATE',
            playCount: 0,
            chords: []
          }
        ]);
      } finally {
        setLoading(false);
      }
    };

    loadRecentSongs();
  }, []);

  const handleFamiliarSongs = () => {
    if (recentSongs && recentSongs.length > 0) {
      const randomSong = recentSongs[Math.floor(Math.random() * recentSongs.length)];
      if (randomSong?.id) {
        navigate(`/player/${randomSong.id}`);
      }
    } else {
      navigate('/search');
    }
  };

  const handleVoiceSearch = () => {
    navigate('/search');
  };

  const handleSongSelect = (songId: number) => {
    if (songId) {
      navigate(`/player/${songId}`);
    }
  };

  const chordProgressions = [
    { name: 'Canon', chords: 'C G Am Em F C F G' },
    { name: 'Pop Ballad', chords: 'G Em C D' },
    { name: 'Blues', chords: 'A D E A' },
    { name: 'Bolero', chords: 'Am Dm G C' },
  ];

  return (
    <Box className="modern-container" sx={{ minHeight: '100vh', position: 'relative' }}>
      {/* Modern Header */}
      <Box className="glass-bg" sx={{ 
        position: 'fixed',
        top: 0,
        width: '100%',
        maxWidth: '430px',
        left: '50%',
        transform: 'translateX(-50%)',
        backdropFilter: 'blur(20px)',
        borderBottom: '1px solid rgba(255, 255, 255, 0.18)',
        zIndex: 10
      }}>
        <Box sx={{ 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'space-between',
          px: 2,
          py: 2
        }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5 }}>
            <Box className="pulse" sx={{
              width: 40,
              height: 40,
              background: 'var(--gradient-primary)',
              borderRadius: 3,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '1.25rem',
              boxShadow: 'var(--shadow-soft)'
            }}>
              <i className="ri-music-2-line" />
            </Box>
            <Typography variant="h5" sx={{ 
              fontFamily: '"Space Grotesk", sans-serif',
              fontWeight: 700, 
              background: 'var(--gradient-primary)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              backgroundClip: 'text'
            }}>
              Guitar Chords VN
            </Typography>
          </Box>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <Chip
              size="small"
              label="Trial: 3"
              icon={<i className="ri-vip-crown-fill" style={{ fontSize: '1rem' }} />}
              sx={{
                background: 'var(--gradient-warm)',
                color: 'white',
                fontSize: '0.75rem',
                px: 1,
                border: '1px solid rgba(255, 255, 255, 0.1)',
                backdropFilter: 'blur(10px)',
                '& .MuiChip-icon': {
                  color: 'white',
                  ml: 0.5
                }
              }}
            />
            <Avatar 
              sx={{ 
                width: 36, 
                height: 36,
                background: 'var(--gradient-accent)',
                border: '2px solid rgba(255, 255, 255, 0.2)',
                boxShadow: 'var(--shadow-soft)'
              }}
            >
              <i className="ri-user-3-line" style={{ fontSize: '1.25rem', color: 'white' }} />
            </Avatar>
          </Box>
        </Box>
      </Box>

      {/* Main Content */}
      <Box sx={{ pt: '5rem', px: 2, pb: 10 }}>
        {/* Welcome Section */}
        <Box sx={{ textAlign: 'center', mb: 4, px: 1 }}>
          <Typography variant="h4" sx={{ 
            fontFamily: '"Space Grotesk", sans-serif',
            fontWeight: 600,
            color: 'white',
            mb: 1,
            textShadow: '0 2px 10px rgba(0,0,0,0.3)'
          }}>
            Khám phá thế giới âm nhạc Việt
          </Typography>
          <Typography variant="body1" sx={{ 
            color: 'rgba(255,255,255,0.8)',
            fontSize: '0.9rem'
          }}>
            Học guitar với hợp âm và lời bài hát tiếng Việt
          </Typography>
        </Box>

        {/* Action Buttons */}
        <Box sx={{ 
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          mb: 4,
          gap: 2
        }}>
          <Button
            onClick={handleVoiceSearch}
            className="glass-card"
            sx={{
              background: 'var(--gradient-accent)',
              color: 'white',
              borderRadius: 5,
              px: 2,
              py: 1.5,
              minWidth: 140,
              display: 'flex',
              alignItems: 'center',
              gap: 1,
              textTransform: 'none',
              fontWeight: 500,
              fontSize: '0.9rem',
              border: 'none',
              '&:hover': {
                transform: 'translateY(-2px)',
                boxShadow: 'var(--shadow-hard)'
              }
            }}
          >
            <i className="ri-mic-line" style={{ fontSize: '1.25rem' }} />
            Tìm Kiếm
          </Button>

          <Button
            onClick={handleFamiliarSongs}
            className="glass-card"
            sx={{
              background: 'var(--gradient-secondary)',
              color: 'white',
              borderRadius: 5,
              px: 2,
              py: 1.5,
              minWidth: 140,
              display: 'flex',
              alignItems: 'center',
              gap: 1,
              textTransform: 'none',
              fontWeight: 500,
              fontSize: '0.9rem',
              border: 'none',
              '&:hover': {
                transform: 'translateY(-2px)',
                boxShadow: 'var(--shadow-hard)'
              }
            }}
          >
            <i className="ri-music-2-line" style={{ fontSize: '1.25rem' }} />
            Quen Thuộc
          </Button>
        </Box>


        {/* Recent Songs */}
        <Box sx={{ mt: 4 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 2, gap: 1 }}>
            <Box sx={{
              width: 32,
              height: 32,
              background: 'var(--gradient-warm)',
              borderRadius: 2.5,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '1rem'
            }}>
              <i className="ri-fire-line" />
            </Box>
            <Typography variant="h5" sx={{ 
              fontFamily: '"Space Grotesk", sans-serif',
              fontWeight: 600,
              color: 'white',
              textShadow: '0 2px 10px rgba(0,0,0,0.3)'
            }}>
              Bài hát nổi bật
            </Typography>
          </Box>

          {loading ? (
            <Box sx={{ p: 4, textAlign: 'center' }}>
              <Box className="spin" sx={{
                width: 40,
                height: 40,
                border: '4px solid rgba(255, 255, 255, 0.3)',
                borderTop: '4px solid var(--gradient-primary)',
                borderRadius: '50%',
                margin: '0 auto',
                mb: 2
              }} />
              <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                Đang tải...
              </Typography>
            </Box>
          ) : (
            <Box sx={{ 
              overflowX: 'auto',
              pb: 2,
              '&::-webkit-scrollbar': { display: 'none' },
              msOverflowStyle: 'none',
              scrollbarWidth: 'none'
            }}>
              <Box sx={{ display: 'flex', gap: 1.5, width: 'max-content' }}>
                {recentSongs && recentSongs.length > 0 ? (
                  recentSongs.map((song, index) => (
                    <Box
                      key={song.id}
                      onClick={() => handleSongSelect(song.id)}
                      className="glass-card slide-in-up"
                      sx={{
                        width: '280px',
                        flexShrink: 0,
                        cursor: 'pointer',
                        overflow: 'hidden',
                        p: 2,
                        position: 'relative',
                        animationDelay: `${index * 0.1}s`,
                        '&:hover': {
                          transform: 'translateY(-8px)',
                          boxShadow: 'var(--shadow-hard)'
                        },
                        '&::before': {
                          content: '""',
                          position: 'absolute',
                          top: 0,
                          left: 0,
                          right: 0,
                          height: '3px',
                          background: 'var(--gradient-primary)',
                          borderRadius: '20px 20px 0 0'
                        }
                      }}
                    >
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 1 }}>
                        <Box sx={{ flex: 1 }}>
                          <Typography 
                            variant="h6" 
                            sx={{ 
                              fontFamily: '"Space Grotesk", sans-serif',
                              fontWeight: 600,
                              fontSize: '1.125rem',
                              mb: 0.5,
                              color: 'white',
                              overflow: 'hidden',
                              textOverflow: 'ellipsis',
                              whiteSpace: 'nowrap'
                            }}
                          >
                            {song.title}
                          </Typography>
                          <Typography 
                            variant="body2" 
                            sx={{
                              fontSize: '0.875rem',
                              color: 'rgba(255,255,255,0.7)',
                              mb: 1,
                              overflow: 'hidden',
                              textOverflow: 'ellipsis',
                              whiteSpace: 'nowrap'
                            }}
                          >
                            {song.artist?.name}
                          </Typography>
                          <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                            <Chip
                              size="small"
                              label={song.difficulty === 'BEGINNER' ? 'Dễ' : song.difficulty === 'INTERMEDIATE' ? 'Trung bình' : 'Khó'}
                              sx={{
                                fontSize: '0.75rem',
                                height: 24,
                                background: song.difficulty === 'BEGINNER' 
                                  ? 'linear-gradient(135deg, #48bb78, #38a169)' 
                                  : song.difficulty === 'INTERMEDIATE'
                                  ? 'linear-gradient(135deg, #ed8936, #dd6b20)'
                                  : 'linear-gradient(135deg, #e53e3e, #c53030)',
                                color: 'white',
                                border: 'none'
                              }}
                            />
                            <Chip
                              size="small"
                              label={`${song.tempo} BPM`}
                              sx={{
                                fontSize: '0.75rem',
                                height: 24,
                                background: 'rgba(255, 255, 255, 0.2)',
                                color: 'rgba(255,255,255,0.8)',
                                border: '1px solid rgba(255, 255, 255, 0.1)'
                              }}
                            />
                          </Box>
                        </Box>
                        <IconButton
                          sx={{
                            width: 48,
                            height: 48,
                            background: 'var(--gradient-accent)',
                            color: 'white',
                            ml: 1,
                            '&:hover': {
                              transform: 'scale(1.1)',
                              background: 'var(--gradient-accent)'
                            }
                          }}
                        >
                          <i className="ri-play-fill" style={{ fontSize: '1.25rem' }} />
                        </IconButton>
                      </Box>
                    </Box>
                  ))
                ) : (
                  <Box sx={{ p: 4, textAlign: 'center', width: '100%' }}>
                    <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                      Chưa có bài hát nào gần đây
                    </Typography>
                  </Box>
                )}
              </Box>
            </Box>
          )}
        </Box>

        {/* Chord Progressions */}
        <Box sx={{ mt: 4 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 2, gap: 1 }}>
            <Box sx={{
              width: 32,
              height: 32,
              background: 'var(--gradient-primary)',
              borderRadius: 2.5,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '1rem'
            }}>
              <i className="ri-music-line" />
            </Box>
            <Typography variant="h5" sx={{ 
              fontFamily: '"Space Grotesk", sans-serif',
              fontWeight: 600,
              color: 'white',
              textShadow: '0 2px 10px rgba(0,0,0,0.3)'
            }}>
              Hợp âm cơ bản
            </Typography>
          </Box>

          <Box sx={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(75px, 1fr))', gap: 1.5 }}>
            {['C', 'G', 'Am', 'F', 'Dm', 'Em'].map((chord, index) => (
              <Box 
                key={chord}
                onClick={() => navigate(`/chord/${chord}`)}
                className="glass-card slide-in-up"
                sx={{
                  aspectRatio: 1,
                  cursor: 'pointer',
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  justifyContent: 'center',
                  position: 'relative',
                  overflow: 'hidden',
                  animationDelay: `${index * 0.1}s`,
                  '&:hover': {
                    transform: 'translateY(-3px) scale(1.05)',
                    borderColor: 'var(--accent)',
                    boxShadow: 'var(--shadow-hard)'
                  },
                  '&::before': {
                    content: '""',
                    position: 'absolute',
                    inset: 0,
                    background: 'var(--gradient-accent)',
                    opacity: 0,
                    transition: 'opacity 0.3s ease'
                  },
                  '&:hover::before': {
                    opacity: 0.1
                  }
                }}
              >
                <Typography 
                  variant="h4" 
                  sx={{ 
                    fontFamily: '"Space Grotesk", sans-serif',
                    fontWeight: 700,
                    color: 'white',
                    fontSize: '1.125rem',
                    zIndex: 1
                  }}
                >
                  {chord}
                </Typography>
                <Typography 
                  variant="body2" 
                  sx={{ 
                    fontSize: '0.75rem',
                    color: 'rgba(255,255,255,0.7)',
                    zIndex: 1
                  }}
                >
                  {chord.includes('m') ? 'Minor' : 'Major'}
                </Typography>
              </Box>
            ))}
          </Box>
        </Box>
      </Box>

      {/* Floating Search Button */}
      <Fab
        color="primary"
        onClick={handleVoiceSearch}
        sx={{
          position: 'fixed',
          bottom: '1rem',
          right: '1rem',
          width: 56,
          height: 56,
          boxShadow: 3
        }}
      >
        <i className="ri-search-2-fill" style={{ fontSize: '1.5rem' }} />
      </Fab>
    </Box>
  );
};

export default MainWindow;
