import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Avatar,
  Chip,
  IconButton,
  Button,
  Fab,
} from '@mui/material';
import { apiService, Song } from '../services/api';
import GuitarLoader from './GuitarLoader';

const MainWindow: React.FC = () => {
  const navigate = useNavigate();
  const [recentSongs, setRecentSongs] = useState<Song[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadRecentSongs = async () => {
      try {
        setLoading(true);
        setError(null);
        const songs = await apiService.getRecentSongs(3);
        setRecentSongs(songs || []);
      } catch (err) {
        console.error('Failed to load recent songs:', err);
        setError('Không thể tải danh sách bài hát gần đây');
        // Fallback to mock data
        setRecentSongs([
          { 
            id: 1, 
            title: 'Nơi Này Có Anh', 
            artist: { id: 1, name: 'Sơn Tùng M-TP' }, 
            lyrics: '',
            tempo: 80,
            difficulty: 'BEGINNER',
            playCount: 0,
            chords: []
          },
          { 
            id: 2, 
            title: 'Chạy Ngay Đi', 
            artist: { id: 1, name: 'Sơn Tùng M-TP' }, 
            lyrics: '',
            tempo: 90,
            difficulty: 'INTERMEDIATE',
            playCount: 0,
            chords: []
          },
          { 
            id: 3, 
            title: 'Có Chắc Yêu Là Đây', 
            artist: { id: 1, name: 'Sơn Tùng M-TP' }, 
            lyrics: '',
            tempo: 85,
            difficulty: 'BEGINNER',
            playCount: 0,
            chords: []
          },
          { 
            id: 4, 
            title: 'Hãy Trao Cho Anh', 
            artist: { id: 1, name: 'Sơn Tùng M-TP' }, 
            lyrics: '',
            tempo: 95,
            difficulty: 'INTERMEDIATE',
            playCount: 0,
            chords: []
          }
        ]);
      } finally {
        setLoading(false);
      }
    };

    loadRecentSongs();
  }, []);

  const handleFamiliarSongs = () => {
    if (recentSongs && recentSongs.length > 0) {
      const randomSong = recentSongs[Math.floor(Math.random() * recentSongs.length)];
      if (randomSong?.id) {
        navigate(`/player/${randomSong.id}`);
      }
    } else {
      navigate('/search');
    }
  };

  const handleVoiceSearch = () => {
    navigate('/search');
  };

  const handleSongSelect = (songId: number) => {
    if (songId) {
      navigate(`/player/${songId}`);
    }
  };

  const chordProgressions = [
    { name: 'Canon', chords: 'C G Am Em F C F G' },
    { name: 'Pop Ballad', chords: 'G Em C D' },
    { name: 'Blues', chords: 'A D E A' },
    { name: 'Bolero', chords: 'Am Dm G C' },
  ];

  return (
    <Box className="modern-container" sx={{ minHeight: '100vh', position: 'relative' }}>
      {/* Modern Header */}
      <Box className="glass-bg" sx={{ 
        position: 'fixed',
        top: 0,
        width: '100%',
        maxWidth: '430px',
        left: '50%',
        transform: 'translateX(-50%)',
        backdropFilter: 'blur(20px)',
        borderBottom: '1px solid rgba(255, 255, 255, 0.18)',
        zIndex: 10
      }}>
        <Box sx={{ 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'space-between',
          px: 2,
          py: 2
        }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5 }}>
            <Box className="pulse" sx={{
              width: 40,
              height: 40,
              background: 'var(--gradient-primary)',
              borderRadius: 3,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '1.25rem',
              boxShadow: 'var(--shadow-soft)'
            }}>
              <i className="ri-music-2-line" />
            </Box>
            <Typography variant="h5" sx={{ 
              fontFamily: '"Space Grotesk", sans-serif',
              fontWeight: 700, 
              background: 'var(--gradient-primary)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              backgroundClip: 'text'
            }}>
              Guitar Chords VN
            </Typography>
          </Box>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <Chip
              size="small"
              label="Trial: 3"
              icon={<i className="ri-vip-crown-fill" style={{ fontSize: '1rem' }} />}
              sx={{
                background: 'var(--gradient-warm)',
                color: 'white',
                fontSize: '0.75rem',
                px: 1,
                border: '1px solid rgba(255, 255, 255, 0.1)',
                backdropFilter: 'blur(10px)',
                '& .MuiChip-icon': {
                  color: 'white',
                  ml: 0.5
                }
              }}
            />
            <Avatar 
              sx={{ 
                width: 36, 
                height: 36,
                background: 'var(--gradient-accent)',
                border: '2px solid rgba(255, 255, 255, 0.2)',
                boxShadow: 'var(--shadow-soft)'
              }}
            >
              <i className="ri-user-3-line" style={{ fontSize: '1.25rem', color: 'white' }} />
            </Avatar>
          </Box>
        </Box>
      </Box>

      {/* Main Content */}
      <Box sx={{ pt: '5rem', px: 2, pb: 10 }}>
        {/* Welcome Section */}
        <Box sx={{ textAlign: 'center', mb: 4, px: 1 }}>
          <Typography variant="h4" sx={{ 
            fontFamily: '"Space Grotesk", sans-serif',
            fontWeight: 600,
            color: 'white',
            mb: 1,
            textShadow: '0 2px 10px rgba(0,0,0,0.3)'
          }}>
            Khám phá thế giới âm nhạc Việt
          </Typography>
          <Typography variant="body1" sx={{ 
            color: 'rgba(255,255,255,0.8)',
            fontSize: '0.9rem'
          }}>
            Học guitar với hợp âm và lời bài hát tiếng Việt
          </Typography>
        </Box>

        {/* Action Buttons */}
        <Box sx={{ 
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          mb: 4,
          gap: 2
        }}>
          <Button
            onClick={handleVoiceSearch}
            className="glass-card"
            sx={{
              background: 'var(--gradient-accent)',
              color: 'white',
              borderRadius: 5,
              px: 2,
              py: 1.5,
              minWidth: 140,
              display: 'flex',
              alignItems: 'center',
              gap: 1,
              textTransform: 'none',
              fontWeight: 500,
              fontSize: '0.9rem',
              border: 'none',
