          <Button
            variant="outlined"
            fullWidth
            onClick={handleFamiliarSongs}
            sx={{
              borderColor: 'primary.main',
              borderWidth: 2,
              color: 'primary.main',
              py: 3,
              borderRadius: 2,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              textTransform: 'none',
              '&:hover': {
                borderWidth: 2,
                borderColor: 'primary.main',
                bgcolor: 'primary.main',
                color: 'white',
                '& .icon-bg': {
                  bgcolor: 'rgba(255,255,255,0.2)'
                }
              }
            }}
          >
            <Box 
              className="icon-bg"
              sx={{
                width: 64,
                height: 64,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                bgcolor: 'rgba(79,70,229,0.1)',
                borderRadius: '50%',
                mb: 1,
                transition: 'all 0.3s'
              }}
            >
              <i className="ri-music-2-line" style={{ fontSize: '2rem' }} />
            </Box>
            <Typography variant="subtitle1" fontWeight={500}>
              Quen Thuộc
            </Typography>
          </Button>
        </Box>


        {/* Recent Songs */}
        <Box sx={{ mt: 4 }}>
          <Typography variant="h6" sx={{ mb: 1.5, fontSize: '1.125rem', fontWeight: 600 }}>
            Bài Gần Đây
          </Typography>

          {loading ? (
            <Box sx={{ p: 4, textAlign: 'center' }}>
              <Typography variant="body2" color="text.secondary">
                Đang tải...
              </Typography>
            </Box>
          ) : (
            <Box sx={{ 
              overflowX: 'auto',
              pb: 2,
              '&::-webkit-scrollbar': { display: 'none' },
              msOverflowStyle: 'none',
              scrollbarWidth: 'none'
            }}>
              <Box sx={{ display: 'flex', gap: 1.5, width: 'max-content' }}>
                {recentSongs && recentSongs.length > 0 ? (
                  recentSongs.map((song, index) => (
                    <Card
                      key={song.id}
                      onClick={() => handleSongSelect(song.id)}
                      sx={{
                        width: '10rem',
                        flexShrink: 0,
                        cursor: 'pointer',
                        overflow: 'hidden',
                        transition: 'all 0.2s',
                        '&:hover': {
                          transform: 'translateY(-2px)',
                          boxShadow: 2
                        }
                      }}
                    >
                      <Box sx={{ 
                        height: '8rem',
                        overflow: 'hidden',
                        bgcolor: 'grey.100'
                      }}>
                        <img 
                          src={`https://readdy.ai/api/search-image?query=acoustic%20guitar%20with%20sheet%20music%20in%20warm%20lighting%2C%20artistic%20composition%2C%20music%20theme%2C%20soft%20focus&width=160&height=128&seq=${index + 2}&orientation=landscape`}
                          alt={song.title}
                          style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                        />
                      </Box>
                      <Box sx={{ p: 1.5 }}>
                        <Typography 
                          variant="subtitle1" 
                          sx={{ 
                            fontWeight: 500,
                            fontSize: '0.875rem',
                            mb: 0.5,
                            overflow: 'hidden',
                            textOverflow: 'ellipsis',
                            whiteSpace: 'nowrap'
                          }}
                        >
                          {song.title}
                        </Typography>
                        <Typography 
                          variant="body2" 
                          color="text.secondary"
                          sx={{
                            fontSize: '0.75rem',
                            overflow: 'hidden',
                            textOverflow: 'ellipsis',
                            whiteSpace: 'nowrap'
                          }}
                        >
                          {song.artist?.name}
                        </Typography>
                      </Box>
                    </Card>
                  ))
                ) : (
                  <Box sx={{ p: 4, textAlign: 'center', width: '100%' }}>
                    <Typography variant="body2" color="text.secondary">
                      Chưa có bài hát nào gần đây
                    </Typography>
                  </Box>
                )}
              </Box>
            </Box>
          )}
        </Box>

        {/* Chord Progressions */}
        <Box sx={{ mt: 3 }}>
          <Typography variant="h6" sx={{ mb: 1.5, fontSize: '1.125rem', fontWeight: 600 }}>
            Vòng Hợp Âm Phổ Biến
          </Typography>

          <Box sx={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 1.5 }}>
            {chordProgressions.map((progression, index) => (
              <Card 
                key={progression.name}
                onClick={() => navigate(`/chord/${progression.name}`)}
                sx={{
                  cursor: 'pointer',
                  p: 1.5,
                  transition: 'all 0.2s',
                  '&:hover': {
                    transform: 'translateY(-1px)',
                    boxShadow: 2
                  }
                }}
              >
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <Box sx={{
                    width: 40,
                    height: 40,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    bgcolor: 'rgba(79,70,229,0.1)',
                    borderRadius: 2,
                    color: 'primary.main'
                  }}>
                    <i className="ri-music-2-line" style={{ fontSize: '1.25rem' }} />
                  </Box>
                  <Box sx={{ ml: 1.5 }}>
                    <Typography 
                      variant="subtitle1" 
                      sx={{ 
                        fontWeight: 500,
                        fontSize: '0.875rem',
                        mb: 0.25
                      }}
                    >
                      {progression.name}
                    </Typography>
                    <Typography 
                      variant="body2" 
                      color="text.secondary"
                      sx={{ 
                        fontSize: '0.75rem'
                      }}
                    >
                      {progression.chords}
                    </Typography>
                  </Box>
                </Box>
              </Card>
            ))}
          </Box>
        </Box>
      </Box>

      {/* Floating Search Button */}
      <Fab
        color="primary"
        onClick={handleVoiceSearch}
        sx={{
          position: 'fixed',
          bottom: '1rem',
          right: '1rem',
          width: 56,
          height: 56,
          boxShadow: 3
        }}
      >
        <i className="ri-search-2-fill" style={{ fontSize: '1.5rem' }} />
      </Fab>
    </Box>
  );
};

export default MainWindow;
