import React from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  Button,
  Card,
  CardContent,
  List,
  ListItem,
  ListItemText,
  ListItemButton,
} from '@mui/material';
import {
  MicRounded,
  FavoriteRounded,
  HistoryRounded,
} from '@mui/icons-material';

interface RecentSong {
  id: string;
  title: string;
  artist: string;
  chords: string[];
}

const MainWindow: React.FC = () => {
  const navigate = useNavigate();

  const recentSongs: RecentSong[] = [
    { id: '1', title: 'Mưa Phi Trường', artist: 'Hoàng Dũng', chords: ['Am', 'Dm', 'G', 'C'] },
    { id: '2', title: 'Nơi Này Có Anh', artist: 'Sơn Tùng M-TP', chords: ['C', 'Am', 'F', 'G'] },
    { id: '3', title: 'Đã Lỡ Yêu Em Nhiều', artist: 'JustaTee', chords: ['Em', 'Am', 'D', 'G'] },
  ];

  const handleFamiliarSongs = () => {
    const randomSong = recentSongs[Math.floor(Math.random() * recentSongs.length)];
    navigate(`/player/${randomSong.id}`);
  };

  const handleVoiceSearch = () => {
    navigate('/search');
  };

  const handleSongSelect = (songId: string) => {
    navigate(`/player/${songId}`);
  };

  return (
    <Container maxWidth="sm" sx={{ py: 4 }}>
      <Box display="flex" flexDirection="column" alignItems="center" gap={4}>
        <Typography variant="h3" component="h1" gutterBottom textAlign="center" fontWeight="bold">
          Guitar Chords
        </Typography>

        <Typography variant="h6" color="text.secondary" textAlign="center">
          Ứng dụng hỗ trợ đệm hát guitar
        </Typography>

        <Box display="flex" flexDirection="column" gap={3} width="100%">
          <Button
            variant="contained"
            size="large"
            startIcon={<FavoriteRounded />}
            onClick={handleFamiliarSongs}
            sx={{ py: 2, fontSize: '1.1rem' }}
          >
            Quen thuộc
          </Button>

          <Button
            variant="contained"
            size="large"
            startIcon={<MicRounded />}
            onClick={handleVoiceSearch}
            sx={{ py: 2, fontSize: '1.1rem' }}
            color="secondary"
          >
            Tìm kiếm bằng giọng nói
          </Button>
        </Box>

        <Card sx={{ width: '100%', mt: 2 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <HistoryRounded />
              Bài gần nhất
            </Typography>
            <List disablePadding>
              {recentSongs.map((song) => (
                <ListItem key={song.id} disablePadding>
                  <ListItemButton onClick={() => handleSongSelect(song.id)}>
                    <ListItemText
                      primary={song.title}
                      secondary={`${song.artist} • ${song.chords.join(' - ')}`}
                    />
                  </ListItemButton>
                </ListItem>
              ))}
            </List>
          </CardContent>
        </Card>
      </Box>
    </Container>
  );
};

export default MainWindow;
