import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { Container } from '@mui/material';
import ErrorBoundary from './components/ErrorBoundary';
import ExtensionNotice from './components/ExtensionNotice';
import MainWindow from './components/MainWindow';
import SearchWindow from './components/SearchWindow';
import MusicPlayer from './components/MusicPlayer';
import ChordTutorial from './components/ChordTutorial';

const theme = createTheme({
  palette: {
    primary: {
      main: '#000000',
    },
    secondary: {
      main: '#666666',
    },
    background: {
      default: '#ffffff',
    },
    text: {
      primary: '#000000',
      secondary: '#666666',
    },
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 300,
    },
    h6: {
      fontWeight: 400,
    },
    subtitle1: {
      fontWeight: 400,
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none',
          borderRadius: 4,
          fontWeight: 400,
        },
      },
    },
  },
});

function App() {
  return (
    <ErrorBoundary>
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <Container maxWidth={false} disableGutters>
          <ExtensionNotice />
        </Container>
        <Router>
          <Routes>
            <Route path="/" element={<MainWindow />} />
            <Route path="/search" element={<SearchWindow />} />
            <Route path="/player/:songId?" element={<MusicPlayer />} />
            <Route path="/chord/:chordName" element={<ChordTutorial />} />
          </Routes>
        </Router>
      </ThemeProvider>
    </ErrorBoundary>
  );
}

export default App;
