import React from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  Card,
  CardContent,
  IconButton,
  Paper,
  Chip,
} from '@mui/material';
import Grid from '@mui/material/Grid2';
import { ArrowBackRounded } from '@mui/icons-material';

interface ChordInfo {
  name: string;
  positions: number[];
  fingers: number[];
  description: string;
  difficulty: 'Dễ' | 'Trung bình' | 'Khó';
  tips: string[];
}

interface ChordDiagramProps {
  positions: number[];
  fingers: number[];
  chordName: string;
}

const ChordDiagram: React.FC<ChordDiagramProps> = ({ positions, fingers, chordName }) => {
  const strings = 6;
  const frets = 5;

  const getFingerColor = (finger: number) => {
    const colors = ['transparent', '#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7'];
    return colors[finger] || 'transparent';
  };

  return (
    <Paper sx={{ p: 3, textAlign: 'center' }}>
      <Typography variant="h5" gutterBottom fontWeight="bold">
        {chordName}
      </Typography>

      <Box sx={{ display: 'inline-block', position: 'relative' }}>
        <svg width="200" height="250" viewBox="0 0 200 250">
          {Array.from({ length: strings }, (_, string) => (
            <line
              key={`string-${string}`}
              x1={30 + string * 28}
              y1={30}
              x2={30 + string * 28}
              y2={230}
              stroke="#333"
              strokeWidth="2"
            />
          ))}

          {Array.from({ length: frets + 1 }, (_, fret) => (
            <line
              key={`fret-${fret}`}
              x1={30}
              y1={30 + fret * 40}
              x2={170}
              y2={30 + fret * 40}
              stroke="#333"
              strokeWidth={fret === 0 ? "4" : "1"}
            />
          ))}

          {positions.map((position, stringIndex) => {
            if (position === 0) {
              return (
                <text
                  key={`open-${stringIndex}`}
                  x={30 + stringIndex * 28}
                  y={20}
                  textAnchor="middle"
                  fontSize="16"
                  fill="#4CAF50"
                  fontWeight="bold"
                >
                  O
                </text>
              );
            }

            if (position === -1) {
              return (
                <text
                  key={`muted-${stringIndex}`}
                  x={30 + stringIndex * 28}
                  y={20}
                  textAnchor="middle"
                  fontSize="16"
                  fill="#F44336"
                  fontWeight="bold"
                >
                  X
                </text>
              );
            }

            return (
              <g key={`fret-${stringIndex}-${position}`}>
                <circle
                  cx={30 + stringIndex * 28}
                  cy={10 + position * 40}
                  r="12"
                  fill={getFingerColor(fingers[stringIndex])}
                  stroke="#333"
                  strokeWidth="2"
                />
                <text
                  x={30 + stringIndex * 28}
                  y={16 + position * 40}
                  textAnchor="middle"
                  fontSize="12"
                  fill="white"
                  fontWeight="bold"
                >
                  {fingers[stringIndex]}
                </text>
              </g>
            );
          })}
        </svg>
      </Box>

      <Box mt={2}>
        <Typography variant="body2" color="text.secondary">
          <strong>Chú thích:</strong>
        </Typography>
        <Typography variant="body2" color="text.secondary">
          O = Dây rỗng | X = Không bấm | Số = Ngón tay
        </Typography>
        <Box display="flex" justifyContent="center" gap={1} mt={1} flexWrap="wrap">
          <Chip label="1: Ngón trỏ" size="small" sx={{ backgroundColor: '#FF6B6B', color: 'white' }} />
