import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  Button,
  Card,
  CardContent,
  List,
  ListItem,
  ListItemText,
  ListItemButton,
  CircularProgress,
  Alert,
} from '@mui/material';
import {
  MicRounded,
  FavoriteRounded,
  HistoryRounded,
} from '@mui/icons-material';
import { apiService, Song } from '../services/api';

const MainWindow: React.FC = () => {
  const navigate = useNavigate();
  const [recentSongs, setRecentSongs] = useState<Song[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadRecentSongs = async () => {
      try {
        setLoading(true);
        setError(null);
        const songs = await apiService.getRecentSongs(3);
        setRecentSongs(songs || []);
      } catch (err) {
        console.error('Failed to load recent songs:', err);
        setError('Không thể tải danh sách bài hát gần đây');
        // Fallback to mock data
        setRecentSongs([
          { 
            id: 1, 
            title: 'Mưa Phi Trường', 
            artist: { id: 1, name: 'Hoàng Dũng' }, 
            lyrics: '',
            tempo: 80,
            difficulty: 'BEGINNER',
            playCount: 0,
            chords: []
          }
        ]);
      } finally {
        setLoading(false);
      }
    };

    loadRecentSongs();
  }, []);

  const handleFamiliarSongs = () => {
    if (recentSongs && recentSongs.length > 0) {
      const randomSong = recentSongs[Math.floor(Math.random() * recentSongs.length)];
      if (randomSong?.id) {
        navigate(`/player/${randomSong.id}`);
      }
    } else {
      navigate('/search');
    }
  };

  const handleVoiceSearch = () => {
    navigate('/search');
  };

  const handleSongSelect = (songId: number) => {
    if (songId) {
      navigate(`/player/${songId}`);
    }
  };

  return (
    <Container maxWidth="sm" sx={{ py: 4 }}>
      <Box display="flex" flexDirection="column" alignItems="center" gap={4}>
        <Typography variant="h3" component="h1" gutterBottom textAlign="center" fontWeight="bold">
          Guitar Chords
        </Typography>

        <Typography variant="h6" color="text.secondary" textAlign="center">
          Ứng dụng hỗ trợ đệm hát guitar
        </Typography>

        <Box display="flex" flexDirection="column" gap={3} width="100%">
          <Button
            variant="contained"
            size="large"
            startIcon={<FavoriteRounded />}
            onClick={handleFamiliarSongs}
            sx={{ py: 2, fontSize: '1.1rem' }}
          >
            Quen thuộc
          </Button>

          <Button
            variant="contained"
            size="large"
            startIcon={<MicRounded />}
            onClick={handleVoiceSearch}
            sx={{ py: 2, fontSize: '1.1rem' }}
            color="secondary"
          >
            Tìm kiếm bằng giọng nói
          </Button>
        </Box>

        <Card sx={{ width: '100%', mt: 2 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <HistoryRounded />
              Bài gần nhất
            </Typography>

            {error && (
              <Alert severity="warning" sx={{ mb: 2 }}>
                {error}
              </Alert>
            )}

            {loading ? (
              <Box display="flex" justifyContent="center" py={2}>
                <CircularProgress size={24} />
              </Box>
            ) : (
              <List disablePadding>
                {recentSongs && recentSongs.length > 0 ? (
                  recentSongs.map((song) => (
                    <ListItem key={song.id} disablePadding>
                      <ListItemButton onClick={() => handleSongSelect(song.id)}>
                        <ListItemText
                          primary={song.title || 'Tên bài hát không có'}
                          secondary={`${song.artist?.name || 'Nghệ sĩ không rõ'} • ${
                            song.chords && song.chords.length > 0 
                              ? song.chords.map(c => c.name || c).join(' - ')
                              : 'Chưa có hợp âm'
                          }`}
                        />
                      </ListItemButton>
                    </ListItem>
                  ))
                ) : (
                  <Typography variant="body2" color="text.secondary" textAlign="center" py={2}>
                    Chưa có bài hát nào gần đây
                  </Typography>
                )}
              </List>
            )}
          </CardContent>
        </Card>
      </Box>
    </Container>
  );
};

export default MainWindow;
