import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Avatar,
  Chip,
  IconButton,
} from '@mui/material';
import {
  MicRounded,
  MusicNoteRounded,
  PersonRounded,
} from '@mui/icons-material';
import { apiService, Song } from '../services/api';
import GuitarLoader from './GuitarLoader';

const MainWindow: React.FC = () => {
  const navigate = useNavigate();
  const [recentSongs, setRecentSongs] = useState<Song[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadRecentSongs = async () => {
      try {
        setLoading(true);
        setError(null);
        const songs = await apiService.getRecentSongs(3);
        setRecentSongs(songs || []);
      } catch (err) {
        console.error('Failed to load recent songs:', err);
        setError('Không thể tải danh sách bài hát gần đây');
        // Fallback to mock data
        setRecentSongs([
          { 
            id: 1, 
            title: 'Mưa Phi Trường', 
            artist: { id: 1, name: 'Hoàng Dũng' }, 
            lyrics: '',
            tempo: 80,
            difficulty: 'BEGINNER',
            playCount: 0,
            chords: []
          }
        ]);
      } finally {
        setLoading(false);
      }
    };

    loadRecentSongs();
  }, []);

  const handleFamiliarSongs = () => {
    if (recentSongs && recentSongs.length > 0) {
      const randomSong = recentSongs[Math.floor(Math.random() * recentSongs.length)];
      if (randomSong?.id) {
        navigate(`/player/${randomSong.id}`);
      }
    } else {
      navigate('/search');
    }
  };

  const handleVoiceSearch = () => {
    navigate('/search');
  };

  const handleSongSelect = (songId: number) => {
    if (songId) {
      navigate(`/player/${songId}`);
    }
  };

  const chordProgressions = [
    { name: 'Canon', chords: 'C G Am Em F C F G', icon: MusicNoteRounded },
    { name: 'Pop Ballad', chords: 'G Em C D', icon: MusicNoteRounded },
    { name: 'Blues', chords: 'A D E A', icon: MusicNoteRounded },
    { name: 'Bolero', chords: 'Am Dm', icon: MusicNoteRounded },
  ];

  return (
    <Box sx={{ 
      minHeight: '100vh', 
      backgroundColor: '#ffffff',
      p: 3
    }}>
      {/* Header */}
      <Box mb={5}>
        <Typography 
          variant="h4" 
          sx={{ 
            color: '#000',
            fontWeight: 300,
            mb: 1
          }}
        >
          Guitar Chords
        </Typography>
        <Typography variant="body2" color="#666">
          Vietnamese guitar learning app
        </Typography>
      </Box>

      {/* Main Actions */}
      <Box mb={5}>
        <Box 
          onClick={handleVoiceSearch}
          sx={{
            p: 4,
            border: '1px solid #e0e0e0',
            borderRadius: 1,
            cursor: 'pointer',
            mb: 2,
            '&:hover': {
              backgroundColor: '#f9f9f9'
            }
          }}
        >
          <Box display="flex" alignItems="center" gap={2}>
            <MicRounded sx={{ fontSize: 24, color: '#666' }} />
            <Typography variant="h6" fontWeight={400}>
              Tìm Kiếm
            </Typography>
          </Box>
          <Typography variant="body2" color="#999" mt={1}>
            Voice search for songs and chords
          </Typography>
        </Box>

        <Box 
          onClick={handleFamiliarSongs}
          sx={{
            p: 4,
            border: '1px solid #e0e0e0',
            borderRadius: 1,
            cursor: 'pointer',
            '&:hover': {
              backgroundColor: '#f9f9f9'
            }
          }}
        >
          <Box display="flex" alignItems="center" gap={2}>
            <MusicNoteRounded sx={{ fontSize: 24, color: '#666' }} />
            <Typography variant="h6" fontWeight={400}>
              Quen Thuộc
            </Typography>
          </Box>
          <Typography variant="body2" color="#999" mt={1}>
            Popular and familiar songs
          </Typography>
        </Box>
      </Box>

      {/* Recent Songs */}
      <Box mb={5}>
        <Typography variant="h6" fontWeight={400} mb={3} color="#000">
          Bài Gần Đây
        </Typography>

        {loading ? (
          <Box textAlign="center" py={4}>
            <Typography variant="body2" color="#999">
              Đang tải...
            </Typography>
          </Box>
        ) : (
          <Box>
            {recentSongs && recentSongs.length > 0 ? (
              recentSongs.slice(0, 3).map((song) => (
                <Box 
                  key={song.id}
                  onClick={() => handleSongSelect(song.id)}
                  sx={{
                    p: 3,
                    borderBottom: '1px solid #f0f0f0',
                    cursor: 'pointer',
                    '&:hover': {
                      backgroundColor: '#f9f9f9'
                    },
                    '&:last-child': {
                      borderBottom: 'none'
                    }
                  }}
                >
                  <Typography variant="subtitle1" fontWeight={400} mb={0.5}>
                    {song.title}
                  </Typography>
                  <Typography variant="body2" color="#666">
                    {song.artist?.name}
                  </Typography>
                </Box>
              ))
            ) : (
              <Box textAlign="center" py={4}>
                <Typography variant="body2" color="#999">
                  Chưa có bài hát nào gần đây
                </Typography>
              </Box>
            )}
          </Box>
        )}
      </Box>

      {/* Chord Progressions */}
      <Box>
        <Typography variant="h6" fontWeight={400} mb={3} color="#000">
          Vòng Hợp Âm Phổ Biến
        </Typography>

        <Box>
          {chordProgressions.map((progression) => (
            <Box 
              key={progression.name}
              sx={{
                p: 3,
                borderBottom: '1px solid #f0f0f0',
                cursor: 'pointer',
                '&:hover': {
                  backgroundColor: '#f9f9f9'
                },
                '&:last-child': {
                  borderBottom: 'none'
                }
              }}
            >
              <Typography variant="subtitle1" fontWeight={400} mb={0.5}>
                {progression.name}
              </Typography>
              <Typography variant="body2" color="#666" fontFamily="monospace">
                {progression.chords}
              </Typography>
            </Box>
          ))}
        </Box>
      </Box>
    </Box>
  );
};

export default MainWindow;
